package data

import (
	"database/sql"
	"errors"
	"regexp"
	"strings"
	"time"
)

type UrlModel struct {
	sql *sql.DB
}

type Url struct {
	Link      string    `json:"link"`
	NewLink   string    `json:"new_link"`
	CreatedAt time.Time `json:"created_at"`
	RemoveAt  time.Time `json:"remove_at"`
}

var (
	DuplicateNewLink = errors.New("duplicate new link")
	NotFound         = errors.New("url not found")
	Expired          = errors.New("url expired")
	UrlNotValid      = errors.New("url not valid")
)

func (u UrlModel) Insert(url string, remove time.Time) (string, error) {
	query := "INSERT INTO url(link, newLink, remove_at) VALUES(?, ?, ?)"

	check, err := regexp.Match(`^(https?|ftp)://[^\s/$.?#].[^\s]*$`, []byte(url))

	if err != nil {
		return "", err
	}

	if !check {
		return "", UrlNotValid
	}

	words := GenerateWords(3)

	args := []any{url, words, remove}

	_, err = u.sql.Exec(query, args...)
	if err != nil {
		switch {
		case strings.Contains(err.Error(), "UNIQUE constraint failed: url.newLink"):
			return "", DuplicateNewLink
		default:
			return "", err
		}
	}

	return words, nil
}

func (u UrlModel) Get(url string) (*Url, error) {
	query := "SELECT link, created_at, remove_at FROM url WHERE newLink = ?"

	var link Url
	err := u.sql.QueryRow(query, url).Scan(&link.Link, &link.CreatedAt, &link.RemoveAt)
	if err != nil {
		switch {
		case strings.Contains(err.Error(), "sql: no rows in result set"):
			return nil, NotFound
		}
		return nil, err
	}

	checkTime := time.Now().Before(link.RemoveAt)

	if !checkTime {
		return nil, Expired
	}

	return &link, nil
}

